export function BadgeType(t: XcmJourneyType) {
  switch (t) {
    case XcmJourneyType.Transfer:
      return html`<span
        class=${tw`text-xs font-medium px-2.5 py-0.5 rounded bg-blue-900 text-blue-300`}
        >${t}</span
      >`;
    case XcmJourneyType.Teleport:
      return html`<span
        class=${tw`text-xs font-medium px-2.5 py-0.5 rounded bg-purple-900 text-purple-300`}
        >${t}</span
      >`;
    case XcmJourneyType.Transact:
      return html`<span
        class=${tw`text-xs font-medium px-2.5 py-0.5 rounded bg-yellow-900 text-yellow-300`}
        >${t}</span
      >`;
    default:
      return html`<span
        class=${tw`text-xs font-medium px-2.5 py-0.5 rounded bg-gray-700 text-gray-300`}
        >${t}</span
      >`;
  }
}
